import { AnecdoteType } from '../App'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom'

const Anecdote = ({
  anecdote,
  anecdotes,
}: {
  anecdote?: AnecdoteType
  anecdotes?: AnecdoteType[]
}) => {
  const id = parseInt(useParams().id || '')

  if (anecdote)
    return (
      <tr>
        <td>
          <Link to={'/anecdotes/' + anecdote.id}>{anecdote.content}</Link>
        </td>

        <td>{anecdote.author}</td>
      </tr>
    )
  const selectedAnecdote = anecdotes && anecdotes.find((a) => a.id === id)
  if (!selectedAnecdote) return null
  return <h1>{selectedAnecdote.content}</h1>
}

export default Anecdote
