import { AnecdoteType } from '../App'
import { useParams } from 'react-router-dom'

const Anecdote = ({
  anecdote,
  anecdotes,
}: {
  anecdote?: AnecdoteType
  anecdotes?: AnecdoteType[]
}) => {
  const id = parseInt(useParams().id || '')

  if (anecdote) return <li>{anecdote.content}</li>
  const selectedAnecdote = anecdotes && anecdotes.find((a) => a.id === id)
  if (!selectedAnecdote) return null
  return <li>{selectedAnecdote.content}</li>
}

export default Anecdote
