import { useState } from 'react'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'

import Menu from './components/Menu'
import AnecdoteList from './components/AnecdoteList'
import Anecdote from './components/Anecdote'
import About from './components/About'
import Footer from './components/Footer'
import CreateNew from './components/CreateNew'

import { Alert } from 'react-bootstrap'

const App = () => {
  const [anecdotes, setAnecdotes] = useState([
    {
      content: 'If it hurts, do it more often',
      author: 'Jez Humble',
      info: 'https://martinfowler.com/bliki/FrequencyReducesDifficulty.html',
      votes: 0,
      id: 1,
    },
    {
      content: 'Premature optimization is the root of all evil',
      author: 'Donald Knuth',
      info: 'http://wiki.c2.com/?PrematureOptimization',
      votes: 0,
      id: 2,
    },
  ] as AnecdoteType[])

  const [notification, setNotification] = useState('')

  const notificationMessage = (message: string) => {
    setNotification(message)
    setTimeout(() => setNotification(''), 5000)
  }

  const addNew = (anecdote: AnecdoteType) => {
    anecdote.id = Math.round(Math.random() * 10000)
    setAnecdotes(anecdotes.concat(anecdote))
  }

  const anecdoteById = (id: number) => anecdotes.find((a) => a.id === id)

  const vote = (id: number) => {
    const anecdote = anecdoteById(id)

    if (anecdote) {
      const voted = {
        ...anecdote,
        votes: anecdote.votes + 1,
      }

      return setAnecdotes(anecdotes.map((a) => (a.id === id ? voted : a)))
    }
    console.error(`anecdote with id: ${id} not found`)
  }

  return (
    <div className='container'>
      <Router>
        <Menu />
        <h1>Software anecdotes</h1>
        {notification && <Alert variant='success'>{notification}</Alert>}
        <Routes>
          <Route
            path='/anecdotes/:id'
            element={<Anecdote anecdotes={anecdotes} />}
          />
          <Route
            path='/anecdotes'
            element={<AnecdoteList anecdotes={anecdotes} />}
          />
          <Route path='/about' element={<About />}></Route>
          <Route
            path='/new'
            element={
              <CreateNew
                addNew={addNew}
                setNotification={notificationMessage}
              />
            }
          />
          <Route path='/' element={<AnecdoteList anecdotes={anecdotes} />} />
        </Routes>
        <Footer />
      </Router>
    </div>
  )
}

export interface AnecdoteType {
  author: string
  info: string
  votes: number
  id?: number
  content: string
}
export default App
