import { AnecdoteType } from '../App'
import { useNavigate } from 'react-router-dom'
import { useField } from '../hooks'

import { Form, Button } from 'react-bootstrap'

const CreateNew = ({
  addNew,
  setNotification,
}: {
  addNew: (anecdote: AnecdoteType) => void
  setNotification: (msg: string) => void
}) => {
  const [content, clearContent] = useField()
  const [author, clearAuthor] = useField()
  const [info, clearInfo] = useField()

  const navigate = useNavigate()

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault()
    addNew({
      content: content.value,
      author: author.value,
      info: info.value,
      votes: 0,
    })
    navigate('/')
    setNotification(`Added ${content.value} by ${author.value}`)
  }

  const handleReset = (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault()
    clearContent()
    clearAuthor()
    clearInfo()
  }

  return (
    <div>
      <h2>create a new anecdote</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>
            content
            <Form.Control {...content} />
          </Form.Label>
          <Form.Label>
            author
            <Form.Control {...author} />
          </Form.Label>
          <div>
            url for more info
            <input {...info} />
          </div>
          <Button variant='primary' type='submit'>
            create
          </Button>
          <Button onClick={handleReset}>reset</Button>
        </Form.Group>
      </Form>
    </div>
  )
}

export default CreateNew
