import { useState } from 'react'
import { AnecdoteType } from '../App'
import { useNavigate } from 'react-router-dom'
import { useField } from '../hooks'

const CreateNew = ({
  addNew,
  setNotification,
}: {
  addNew: (anecdote: AnecdoteType) => void
  setNotification: (msg: string) => void
}) => {
  const content = useField()
  const author = useField()
  const info = useField()

  const navigate = useNavigate()

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault()
    addNew({
      content: content.value,
      author: author.value,
      info: info.value,
      votes: 0,
    })
    navigate('/')
    setNotification(`Added ${content.value} by ${author.value}`)
  }

  const handleRest = (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    e.preventDefault()
  }

  return (
    <div>
      <h2>create a new anecdote</h2>
      <form onSubmit={handleSubmit}>
        <div>
          content
          <input {...content} />
        </div>
        <div>
          author
          <input {...author} />
        </div>
        <div>
          url for more info
          <input {...info} />
        </div>
        <button>create</button>
        <button onClick={(e) => {}}>reset</button>
      </form>
    </div>
  )
}

export default CreateNew
